import Application from 'Gus/Kernel/Application'
import 'colors'

export default class ConsoleOutput
{

    /**
     * Constructor of the ConsoleOutput class.
     *
     * @classdesc Print weird things in the console.
     */
    constructor(protected app: Application)
    {
        // ...
    }

    print(...data: any[])
    {
        console.log(...data)
    }

    printError(error: any, ...otherErrors: any[])
    {
        if (typeof error === 'string')
            console.error(`\n${error}\n`.red)
        else
            console.error(error, ...otherErrors)
    }

    printLogo(){
        var logo =
        (`
                       ,......                                   
                    ,+:::::::::+                          
                  ;:::'+++++++;::'  :; '''':+             
                 +::+++#++,,,,,''::++:+'''''',:'          
                ;::+++#+',,,,,,,,+::::''''';+   +         
                +::++#+,,,,,,,,,,+::+,'         +         
                +::++;,,,,,,,,,,,+::;           '         
                 ;:;+,,,,,,,,,,,+::+           +          
                  '::++,,,,,,,;+::+           +           
                    ':::;'+'::::'            ;            
                       '''''''               '            
                                             +         +  
                                              '       +   
                                               +.  .+,    
                                                 ''       `)
        // End of logo
        // Please don't destroy this, it took us hours to do.
    
    
        console.log(
          '\n' + logo.blue +
          '\n' + (' ').repeat(16 - this.app.package.version.length) +
          `-- Gus ${this.app.package.version} - Proudly maintained by Gus Project --\n`.bold
        )
    }

    
}
