import * as Discord from 'discord.js'
import Arguments from 'Handles/Utils/Arguments'
import Context from 'Handles/Command/CommandContext'
import Argument = Handles.CommandArgument
import Permission = Handles.CommandPermission

export default abstract class CommandHandler {

    static activated: boolean = true
    public allowDM: boolean = true
    public command: string = 'Not defined'
    public desc: string = 'Not defined'
    public args: Argument[] = []
    public props: {} = {}
    public permissions: Permission[] = []
    public aliases: string[] = []
    public usage: string = 'Not defined'

    abstract execute(context: Context, args: Arguments)

}
