import fs = require('fs')
import path = require('path')

export default class ConfigLoader {
    config: any = {}
    keys = {
        'discordAPItoken': 'string',
        'botname': 'string',
        'version': 'string',
        'prefix': 'string',
        'lang': 'string',
        'status': 'string',
        'translate': 'boolean',
        'dbPath': 'string',
        'XPgived': 'array',
        'superadmins': 'array'
    }

    /**
     * Initialize configuration.
     */
    constructor() {
        this.loadConfig()
        this.checkKeys()
        return this.config
    }

    /**
     * Load the configuration file.
     */
    loadConfig() {
        try {
            try {
                this.config = require('../../../../../config.dev.json')
            } catch (ex) {
                this.config = require('../../../../../config.json')
            }
        } catch (ex) {
            throw new Error('Configuration error: ' + ex)
        }
    }

    /**
     * Check if configuration keys are valid.
     */
    checkKeys() {
        try {
            for (var key in this.config) {
                this.checkKey(key)
            }
        } catch (ex) {
            throw new Error('Configuration error: ' + ex)
        }
    }

    /**
     * Check if a configuration key is valid.
     * 
     * @param key Configuration key
     */
    checkKey(key) {
        var value = this.config[key]
        if (this.keys[key]) {
            switch (this.keys[key]) {
                case 'array':
                    if (!(value instanceof Array)) { throw '"' + key + '" property must be an unordered list' }
                    break
                case 'string':
                    if (typeof value !== 'string') { throw '"' + key + '" property must be a doublequoted text' }
                    break
                case 'boolean':
                    if (typeof value !== 'boolean') { throw '"' + key + '" property must be a boolean' }
                    break
                default:
                    throw '"' + key + '" is not a valid property'
            }
        } else throw '"' + key + '" is not a valid property'
    }
}
