export default class Arguments {

    args: Array<string | number> = []
    props: Array<string | number> = []

    /**
     * Constructor of the Selector class.
     */
    constructor(dataList: any) {
        this.args = dataList._
        this.props = dataList
        delete this.props['_']
    }

    get(id: number): string | number | undefined {
        return this.args[id]
    }

    getAll(): Array<string | number> {
        return this.args
    }

    getProp(name: string): string | number | undefined {
        return this.props[name]
    }

    getAllProps(): Array<string | number> {
        return this.props
    }


}
