/**
 * Parse float as a number
 * 
 * @param value Value to parse
 */
export function parseFloat(value: any) {
  if (/^(\-|\+)?([0-9]+(\.[0-9]+)?|Infinity)$/
    .test(value))
    return Number(value)
  return NaN
}

export function fancyTimeFormat(secs: number): string {
  let hours = ~~(secs / 3600),
    minutes = ~~((secs % 3600) / 60),
    seconds = secs % 60,
    ret = ''

  if (hours > 0) ret += '' + hours + ':' + (minutes < 10 ? '0' : '')
  ret += '' + minutes + ':' + (seconds < 10 ? '0' : '') + '' + seconds
  return ret
}
