/*
Rank mathematical functions :
    X = 25LÂ² - 25L
    L = Math.floor(25 + Math.sqrt(625 + 100 * xp)) / 50
*/


/**
 * Convert XP in levels
 * @param xp XP to convert in Level
 */
export function xpToLVL(xp: number): number {
    return Math.floor(Math.floor(25 + Math.sqrt(625 + 100 * xp)) / 50)
}

/**
 * Convert levels in XP
 * @param lvl Level to convert in XP
 */
export function lvlToXP(lvl: number): number {
    return 25 * lvl * lvl - 25 * lvl
}

/**
 * Get current Level XP
 * @param lvl 
 * @param xp 
 */
export function currentLvlXP(xp: number) {
    return xp - xpToLVL(xp)
}
