export function getRandomInt(min: number, max: number): number {
    return Math.floor(Math.random() * (max - min + 1)) + min
}

export function replaceAll(str: string, search: string | Array<string>, replacement: string | Array<string>) {
    if (typeof search === 'string' && typeof replacement === 'string')
        str = str.replace(new RegExp(search, 'g'), replacement)
    else if (search instanceof Array && replacement instanceof Array) {
        search.forEach((value, index) => {
            str = str.replace(new RegExp(value, 'g'), replacement[index])
        })
    }
    return str
}

export function areEqual(...variables: any[]) {
    var len = arguments.length
    for (var i = 1; i < len; i++) {
        if (arguments[i] === null || arguments[i] !== arguments[i - 1])
            return false
    }
    return true
}

export function fancyTimeFormat(secs: number): string {
    let hours = ~~(secs / 3600),
        minutes = ~~((secs % 3600) / 60),
        seconds = secs % 60,
        ret = ''

    if (hours > 0) ret += '' + hours + ':' + (minutes < 10 ? '0' : '')
    ret += '' + minutes + ':' + (seconds < 10 ? '0' : '') + '' + seconds
    return ret
}
